import React, { useState, useEffect, useMemo } from 'react';

const TicketList = ({ onViewDetails }) => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortBy, setSortBy] = useState('createdAt');
  const [sortOrder, setSortOrder] = useState('desc'); // 'asc' | 'desc'
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);

  useEffect(() => {
    fetchTickets();
  }, []);

  const fetchTickets = async () => {
    try {
      const response = await fetch('/api/tickets');
      if (!response.ok) {
        throw new Error('Failed to fetch tickets');
      }
      const data = await response.json();
      setTickets(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteTicket = async (id) => {
    if (!window.confirm('Delete this ticket?')) return;
    try {
      const response = await fetch(`/api/tickets/${id}`, { method: 'DELETE' });
      if (!response.ok) throw new Error('Failed to delete ticket');
      await fetchTickets();
    } catch (err) {
      setError(err.message);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const getStatusClass = (status) => {
    return `status-badge status-${status.toLowerCase()}`;
  };

  const getPriorityClass = (priority) => {
    return priority ? `priority-${priority.toLowerCase()}` : '';
  };

  if (loading) return <div className="container">Loading tickets...</div>;
  if (error) return <div className="container">Error: {error}</div>;

  const compareValues = (a, b) => {
    if (a == null && b == null) return 0;
    if (a == null) return -1;
    if (b == null) return 1;
    if (typeof a === 'string' && typeof b === 'string') {
      return a.localeCompare(b, undefined, { sensitivity: 'base' });
    }
    if (a instanceof Date && b instanceof Date) {
      return a - b;
    }
    return a < b ? -1 : a > b ? 1 : 0;
  };

  const sortedTickets = useMemo(() => {
    const arr = [...tickets];
    arr.sort((x, y) => {
      let a;
      let b;
      switch (sortBy) {
        case 'title':
          a = x.title; b = y.title; break;
        case 'status':
          a = x.status; b = y.status; break;
        case 'priority':
          a = x.priority; b = y.priority; break;
        case 'category':
          a = x.category; b = y.category; break;
        case 'createdAt':
        default:
          a = x.createdAt ? new Date(x.createdAt) : null;
          b = y.createdAt ? new Date(y.createdAt) : null;
          break;
      }
      const cmp = compareValues(a, b);
      return sortOrder === 'asc' ? cmp : -cmp;
    });
    return arr;
  }, [tickets, sortBy, sortOrder]);

  const totalItems = sortedTickets.length;
  const totalPages = Math.max(1, Math.ceil(totalItems / pageSize));
  const safePage = Math.min(currentPage, totalPages);
  const startIdx = (safePage - 1) * pageSize;
  const endIdx = Math.min(startIdx + pageSize, totalItems);
  const pageItems = sortedTickets.slice(startIdx, endIdx);

  const handleChangeSortBy = (e) => { setSortBy(e.target.value); setCurrentPage(1); };
  const handleToggleSortOrder = () => { setSortOrder(o => (o === 'asc' ? 'desc' : 'asc')); };
  const handlePageSize = (e) => { setPageSize(Number(e.target.value)); setCurrentPage(1); };
  const gotoPage = (p) => { if (p >= 1 && p <= totalPages) setCurrentPage(p); };

  return (
    <div className="container">
      <h1>All Tickets</h1>
      {tickets.length === 0 ? (
        <div className="card">
          <p>No tickets found. <a href="/create">Create the first ticket</a></p>
        </div>
      ) : (
        <div>
          <div className="card" style={{ marginBottom: '1rem' }}>
            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.75rem', alignItems: 'center' }}>
              <div>
                <label htmlFor="sortBy" style={{ marginRight: 8 }}>Sort by</label>
                <select id="sortBy" data-testid="ticketlist-sortby" value={sortBy} onChange={handleChangeSortBy}>
                  <option value="createdAt">Created Date</option>
                  <option value="title">Title</option>
                  <option value="status">Status</option>
                  <option value="priority">Priority</option>
                  <option value="category">Category</option>
                </select>
              </div>
              <button data-testid="ticketlist-toggleorder" className="btn-primary" onClick={handleToggleSortOrder}>
                {sortOrder === 'asc' ? 'Asc ↑' : 'Desc ↓'}
              </button>
              <div style={{ marginLeft: 'auto' }}>
                <label htmlFor="pageSize" style={{ marginRight: 8 }}>Per page</label>
                <select id="pageSize" data-testid="ticketlist-pagesize" value={pageSize} onChange={handlePageSize}>
                  <option value={5}>5</option>
                  <option value={10}>10</option>
                  <option value={20}>20</option>
                  <option value={50}>50</option>
                </select>
              </div>
            </div>
            <div style={{ marginTop: 8, color: '#666', fontSize: 14 }}>
              Showing {totalItems === 0 ? 0 : startIdx + 1}–{endIdx} of {totalItems}
            </div>
          </div>

          {pageItems.map(ticket => (
            <div key={ticket.id} className="card">
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                <div style={{ flex: 1 }}>
                  <h3 style={{ margin: '0 0 0.5rem 0' }}>{ticket.title}</h3>
                  <p style={{ margin: '0 0 1rem 0', color: '#666' }}>{ticket.description}</p>
                  <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
                    <span className={getStatusClass(ticket.status)}>{ticket.status}</span>
                    {ticket.priority && (
                      <span className={getPriorityClass(ticket.priority)}>
                        Priority: {ticket.priority}
                      </span>
                    )}
                    {ticket.category && (
                      <span style={{ color: '#666' }}>Category: {ticket.category}</span>
                    )}
                  </div>
                  <div style={{ marginTop: '0.5rem', fontSize: '0.9rem', color: '#666' }}>
                    {ticket.submitter ? `Submitter: ${ticket.submitter.username || ticket.submitter.id}` : 'Submitter: —'} • Created: {ticket.createdAt ? formatDate(ticket.createdAt) : '—'}
                    {ticket.assignedAgent && (
                      <> • Assigned: {ticket.assignedAgent.username || ticket.assignedAgent.id}</>
                    )}
                  </div>
                </div>
                <div style={{ display: 'flex', gap: '0.5rem', marginLeft: '1rem' }}>
                  <button 
                    className="btn-primary" 
                    onClick={() => onViewDetails(ticket.id)}
                  >
                    View Details
                  </button>
                  <button 
                    className="btn-danger" 
                    onClick={() => deleteTicket(ticket.id)}
                    style={{ backgroundColor: '#dc3545', color: '#fff' }}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}

          <div className="card" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', justifyContent: 'center' }}>
            <button className="btn-primary" onClick={() => gotoPage(currentPage - 1)} disabled={currentPage <= 1} data-testid="ticketlist-prev">Prev</button>
            {Array.from({ length: totalPages }).map((_, i) => {
              const p = i + 1;
              const isActive = p === safePage;
              return (
                <button
                  key={p}
                  className="btn-primary"
                  style={isActive ? { backgroundColor: '#0d6efd', color: '#fff' } : undefined}
                  onClick={() => gotoPage(p)}
                  data-testid={`ticketlist-page-${p}`}
                >
                  {p}
                </button>
              );
            })}
            <button className="btn-primary" onClick={() => gotoPage(currentPage + 1)} disabled={currentPage >= totalPages} data-testid="ticketlist-next">Next</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TicketList;
