import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        if (response.status === 404) {
          // Backend not available, use demo mode
          handleDemoLogin();
          return;
        }
        throw new Error('Invalid username or password');
      }

      const userData = await response.json();
      
      // Store user data in localStorage
      localStorage.setItem('user', JSON.stringify(userData));
      
      // Determine role and redirect accordingly
      const userRole = userData.role || 'user'; // Default to 'user' if role is not specified
      
      if (userRole.toLowerCase() === 'admin') {
        // Redirect to admin portal
        window.location.href = '/admin';
      } else {
        // Redirect to user portal
        window.location.href = '/user';
      }
      
      // Call onLogin callback if provided
      if (onLogin) {
        onLogin(userData);
      }

    } catch (err) {
      if (err.name === 'TypeError' && err.message.includes('fetch')) {
        // Network error - backend not available, use demo mode
        handleDemoLogin();
      } else {
        setError(err.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = () => {
    // Demo authentication logic
    let userData = null;
    
    if (formData.username === 'admin' && formData.password === 'admin123') {
      userData = { id: 1, username: 'admin', role: 'admin' };
    } else if (formData.username === 'user' && formData.password === 'user123') {
      userData = { id: 2, username: 'user', role: 'user' };
    } else {
      setError('Demo credentials: admin/admin123 or user/user123');
      return;
    }
    
    localStorage.setItem('user', JSON.stringify(userData));
    
    if (userData.role === 'admin') {
      window.location.href = '/admin';
    } else {
      window.location.href = '/user';
    }
    
    if (onLogin) {
      onLogin(userData);
    }
  };

  return (
    <div className="container" style={{ maxWidth: '400px', margin: '2rem auto' }}>
      <div className="card">
        <h2 style={{ textAlign: 'center', marginBottom: '1.5rem' }}>Login</h2>
        
        {error && (
          <div style={{ 
            backgroundColor: '#f8d7da', 
            color: '#721c24', 
            padding: '0.75rem', 
            borderRadius: '4px', 
            marginBottom: '1rem' 
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '1rem' }}>
            <label htmlFor="username" style={{ display: 'block', marginBottom: '0.5rem' }}>
              Username:
            </label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #ddd',
                borderRadius: '4px',
                fontSize: '1rem'
              }}
            />
          </div>

          <div style={{ marginBottom: '1.5rem' }}>
            <label htmlFor="password" style={{ display: 'block', marginBottom: '0.5rem' }}>
              Password:
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #ddd',
                borderRadius: '4px',
                fontSize: '1rem'
              }}
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="btn-primary"
            style={{
              width: '100%',
              padding: '0.75rem',
              fontSize: '1rem',
              marginBottom: '1rem'
            }}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <div style={{ textAlign: 'center' }}>
          <p>Don't have an account? <a href="/signup">Sign up here</a></p>
        </div>

        <div style={{ 
          marginTop: '1.5rem', 
          padding: '1rem', 
          backgroundColor: '#f8f9fa', 
          borderRadius: '4px',
          fontSize: '0.9rem' 
        }}>
          <h4>Demo Credentials:</h4>
          <p><strong>Admin:</strong> admin / admin123</p>
          <p><strong>User:</strong> user / user123</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
