import React from 'react';
import TicketList from './TicketList';
import CreateTicket from './CreateTicket';
import TicketDetails from './TicketDetails';
import UserManagement from './UserManagement';
import CategoryManagement from './CategoryManagement';
import PriorityManagement from './PriorityManagement';
import KnowledgeBaseManagement from './KnowledgeBaseManagement';

const AdminPortal = () => {
  const [currentView, setCurrentView] = React.useState('dashboard');
  const [selectedTicketId, setSelectedTicketId] = React.useState(null);
  const [dashboardStats, setDashboardStats] = React.useState(null);
  const [statsLoading, setStatsLoading] = React.useState(false);
  const [statsError, setStatsError] = React.useState(null);
  const [systemHealth, setSystemHealth] = React.useState(null);
  const [healthLoading, setHealthLoading] = React.useState(false);

  React.useEffect(() => {
    if (currentView === 'dashboard') {
      fetchDashboardStats();
    }
  }, [currentView]);

  const fetchDashboardStats = async () => {
    setStatsLoading(true);
    setStatsError(null);
    try {
      const response = await fetch('/api/dashboard/stats');
      if (!response.ok) {
        throw new Error('Failed to fetch dashboard statistics');
      }
      const data = await response.json();
      setDashboardStats(data);
    } catch (error) {
      setStatsError(error.message);
    } finally {
      setStatsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleViewDetails = (ticketId) => {
    setSelectedTicketId(ticketId);
    setCurrentView('ticket-details');
  };

  const handleBackToTickets = () => {
    setSelectedTicketId(null);
    setCurrentView('tickets');
  };

  const renderContent = () => {
    switch (currentView) {
      case 'tickets':
        return <TicketList onViewDetails={handleViewDetails} />;
      case 'ticket-details':
        return <TicketDetails 
          ticketId={selectedTicketId} 
          onStatusUpdate={() => {}} 
          onBack={handleBackToTickets} 
        />;
      case 'create-ticket':
        return <CreateTicket />;
      case 'users':
        return <UserManagement />;
      case 'categories':
        return <CategoryManagement />;
      case 'priorities':
        return <PriorityManagement />;
      case 'knowledge-base':
        return <KnowledgeBaseManagement />;
      default:
        return (
          <div className="container">
            <h1>Admin Dashboard</h1>
            
            {statsLoading && (
              <div className="card">
                <p>Loading dashboard statistics...</p>
              </div>
            )}
            
            {statsError && (
              <div className="card" style={{ border: '1px solid #dc3545', backgroundColor: '#f8d7da' }}>
                <h3 style={{ color: '#721c24' }}>Error Loading Statistics</h3>
                <p style={{ color: '#721c24' }}>{statsError}</p>
                <button onClick={fetchDashboardStats} className="btn-primary">Retry</button>
              </div>
            )}
            
            {dashboardStats && (
              <>
                {/* Key Metrics Cards */}
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', marginBottom: '2rem' }}>
                  <div className="card" style={{ backgroundColor: '#e3f2fd', border: '1px solid #2196f3' }}>
                    <h3 style={{ color: '#1565c0', margin: '0 0 0.5rem 0' }}>Total Tickets</h3>
                    <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1565c0' }}>{dashboardStats.totalTickets}</div>
                  </div>
                  <div className="card" style={{ backgroundColor: '#e8f5e8', border: '1px solid #4caf50' }}>
                    <h3 style={{ color: '#2e7d32', margin: '0 0 0.5rem 0' }}>Total Users</h3>
                    <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#2e7d32' }}>{dashboardStats.totalUsers}</div>
                  </div>
                  <div className="card" style={{ backgroundColor: '#fff3e0', border: '1px solid #ff9800' }}>
                    <h3 style={{ color: '#f57c00', margin: '0 0 0.5rem 0' }}>High Priority</h3>
                    <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#f57c00' }}>{dashboardStats.priorityCounts?.HIGH || 0}</div>
                  </div>
                  <div className="card" style={{ backgroundColor: '#ffebee', border: '1px solid #f44336' }}>
                    <h3 style={{ color: '#c62828', margin: '0 0 0.5rem 0' }}>Open Tickets</h3>
                    <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#c62828' }}>
                      {(dashboardStats.statusCounts?.NEW || 0) + (dashboardStats.statusCounts?.IN_PROGRESS || 0)}
                    </div>
                  </div>
                </div>

                {/* Charts Section */}
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1rem', marginBottom: '2rem' }}>
                  {/* Ticket Status Breakdown */}
                  <div className="card">
                    <h3>Ticket Status Distribution</h3>
                    <div style={{ marginTop: '1rem' }}>
                      {dashboardStats.statusCounts && Object.entries(dashboardStats.statusCounts).map(([status, count]) => (
                        <div key={status} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '0.5rem' }}>
                          <span style={{ fontWeight: '500' }}>{status.replace('_', ' ')}</span>
                          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <div style={{
                              width: `${Math.max((count / dashboardStats.totalTickets) * 100, 5)}%`,
                              height: '20px',
                              backgroundColor: status === 'NEW' ? '#2196f3' : 
                                             status === 'IN_PROGRESS' ? '#ff9800' : 
                                             status === 'RESOLVED' ? '#4caf50' : '#9e9e9e',
                              borderRadius: '10px',
                              minWidth: '20px'
                            }}></div>
                            <span style={{ fontWeight: 'bold', minWidth: '30px' }}>{count}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Priority Breakdown */}
                  <div className="card">
                    <h3>Priority Distribution</h3>
                    <div style={{ marginTop: '1rem' }}>
                      {dashboardStats.priorityCounts && Object.entries(dashboardStats.priorityCounts).map(([priority, count]) => (
                        <div key={priority} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '0.5rem' }}>
                          <span style={{ fontWeight: '500' }}>{priority}</span>
                          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <div style={{
                              width: `${Math.max((count / dashboardStats.totalTickets) * 100, 5)}%`,
                              height: '20px',
                              backgroundColor: priority === 'HIGH' ? '#f44336' : 
                                             priority === 'MEDIUM' ? '#ff9800' : '#4caf50',
                              borderRadius: '10px',
                              minWidth: '20px'
                            }}></div>
                            <span style={{ fontWeight: 'bold', minWidth: '30px' }}>{count}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>

                {/* Recent Activity and Alerts */}
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))', gap: '1rem', marginBottom: '2rem' }}>
                  {/* Recent Tickets */}
                  <div className="card">
                    <h3>Recent Tickets</h3>
                    <div style={{ marginTop: '1rem' }}>
                      {dashboardStats.recentTickets?.length > 0 ? (
                        dashboardStats.recentTickets.map(ticket => (
                          <div key={ticket.id} style={{ 
                            padding: '0.75rem', 
                            border: '1px solid #e0e0e0', 
                            borderRadius: '4px', 
                            marginBottom: '0.5rem',
                            cursor: 'pointer',
                            transition: 'background-color 0.2s'
                          }}
                          onClick={() => handleViewDetails(ticket.id)}
                          onMouseEnter={(e) => e.target.style.backgroundColor = '#f5f5f5'}
                          onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
                          >
                            <div style={{ fontWeight: '600', marginBottom: '0.25rem' }}>#{ticket.id} - {ticket.title}</div>
                            <div style={{ fontSize: '0.85rem', color: '#666' }}>
                              Status: {ticket.status} | Priority: {ticket.priority}
                            </div>
                          </div>
                        ))
                      ) : (
                        <p style={{ color: '#666', fontStyle: 'italic' }}>No recent tickets</p>
                      )}
                    </div>
                  </div>

                  {/* High Priority Alerts */}
                  <div className="card" style={{ border: '1px solid #ff9800' }}>
                    <h3 style={{ color: '#f57c00' }}>High Priority Alerts</h3>
                    <div style={{ marginTop: '1rem' }}>
                      {dashboardStats.highPriorityTickets?.length > 0 ? (
                        dashboardStats.highPriorityTickets.map(ticket => (
                          <div key={ticket.id} style={{ 
                            padding: '0.75rem', 
                            border: '1px solid #ffcc02', 
                            borderRadius: '4px', 
                            marginBottom: '0.5rem',
                            backgroundColor: '#fff8e1',
                            cursor: 'pointer'
                          }}
                          onClick={() => handleViewDetails(ticket.id)}
                          >
                            <div style={{ fontWeight: '600', marginBottom: '0.25rem', color: '#e65100' }}>
                              🚨 #{ticket.id} - {ticket.title}
                            </div>
                            <div style={{ fontSize: '0.85rem', color: '#bf360c' }}>
                              Status: {ticket.status} | Needs Attention
                            </div>
                          </div>
                        ))
                      ) : (
                        <p style={{ color: '#666', fontStyle: 'italic' }}>No high priority tickets</p>
                      )}
                    </div>
                  </div>
                </div>

                {/* User Statistics */}
                <div className="card" style={{ marginBottom: '2rem' }}>
                  <h3>User Role Distribution</h3>
                  <div style={{ display: 'flex', gap: '2rem', marginTop: '1rem', flexWrap: 'wrap' }}>
                    {dashboardStats.roleCounts && Object.entries(dashboardStats.roleCounts).map(([role, count]) => (
                      <div key={role} style={{ textAlign: 'center' }}>
                        <div style={{ 
                          fontSize: '1.5rem', 
                          fontWeight: 'bold', 
                          color: role === 'ADMIN' ? '#f44336' : role === 'AGENT' ? '#2196f3' : '#4caf50' 
                        }}>
                          {count}
                        </div>
                        <div style={{ fontSize: '0.9rem', color: '#666' }}>{role}S</div>
                      </div>
                    ))}
                  </div>
                </div>
              </>
            )}
            
            {/* Quick Actions */}
            <div className="card">
              <h2>Quick Actions</h2>
              <p>Use the navigation menu to manage tickets, users, and system settings.</p>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', marginTop: '2rem' }}>
                <div className="card" style={{ cursor: 'pointer' }} onClick={() => setCurrentView('tickets')}>
                  <h3>All Tickets</h3>
                  <p>View and manage all support tickets</p>
                </div>
                <div className="card" style={{ cursor: 'pointer' }} onClick={() => setCurrentView('users')}>
                  <h3>User Management</h3>
                  <p>Manage user accounts and permissions</p>
                </div>
                <div className="card" style={{ cursor: 'pointer' }} onClick={() => setCurrentView('categories')}>
                  <h3>Categories</h3>
                  <p>Manage ticket categories</p>
                </div>
                <div className="card" style={{ cursor: 'pointer' }} onClick={() => setCurrentView('knowledge-base')}>
                  <h3>Knowledge Base</h3>
                  <p>Manage help articles and FAQs</p>
                </div>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div>
      <nav style={{ 
        backgroundColor: '#2c3e50', 
        color: 'white', 
        padding: '1rem',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h2>Admin Portal</h2>
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          <button 
            onClick={() => setCurrentView('dashboard')}
            style={{ background: 'none', border: '1px solid white', color: 'white', padding: '0.5rem 1rem', borderRadius: '4px' }}
          >
            Dashboard
          </button>
          <button 
            onClick={() => setCurrentView('tickets')}
            style={{ background: 'none', border: '1px solid white', color: 'white', padding: '0.5rem 1rem', borderRadius: '4px' }}
          >
            Tickets
          </button>
          <button 
            onClick={() => setCurrentView('users')}
            style={{ background: 'none', border: '1px solid white', color: 'white', padding: '0.5rem 1rem', borderRadius: '4px' }}
          >
            Users
          </button>
          <button 
            onClick={() => setCurrentView('categories')}
            style={{ background: 'none', border: '1px solid white', color: 'white', padding: '0.5rem 1rem', borderRadius: '4px' }}
          >
            Categories
          </button>
          <button 
            onClick={handleLogout}
            style={{ background: '#e74c3c', border: 'none', color: 'white', padding: '0.5rem 1rem', borderRadius: '4px' }}
          >
            Logout
          </button>
        </div>
      </nav>
      {renderContent()}
    </div>
  );
};

export default AdminPortal;
