import React, { useState, useEffect } from 'react';

const CreateTicket = ({ onSuccess }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    priorityId: '',
    categoryId: ''
  });
  const [priorities, setPriorities] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  useEffect(() => {
    // load lookups
    const load = async () => {
      try {
        const [pRes, cRes] = await Promise.all([
          fetch('/api/lookups/priorities'),
          fetch('/api/lookups/categories')
        ]);
        const pJson = await pRes.json();
        const cJson = await cRes.json();
        setPriorities(pJson);
        setCategories(cJson);
        setFormData(fd => ({
          ...fd,
          priorityId: pJson[0]?.id || '',
          categoryId: cJson[0]?.id || ''
        }));
      } catch (err) {
        setError('Failed to load reference data');
      }
    };
    load();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const payload = {
        title: formData.title,
        description: formData.description,
        priorityId: formData.priorityId ? Number(formData.priorityId) : undefined,
        categoryId: formData.categoryId ? Number(formData.categoryId) : undefined
      };
      const response = await fetch('/api/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        let errorMsg = 'Failed to create ticket';
        try {
          const errTxt = await response.text();
          if (errTxt) {
            const errJson = JSON.parse(errTxt);
            errorMsg = errJson.error || errJson.message || errorMsg;
          }
        } catch (_) { /* ignore parse issues */ }
        throw new Error(errorMsg);
      }

      // Safe parse success body
      let ticket;
      try {
        const txt = await response.text();
        ticket = txt ? JSON.parse(txt) : null;
      } catch (_) {
        ticket = null;
      }
      if (onSuccess) {
        onSuccess(ticket);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Create New Ticket</h1>
      <div className="card">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="title">Title *</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              maxLength="255"
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description *</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              maxLength="1000"
              placeholder="Describe the issue in detail..."
            />
          </div>

          <div className="form-group">
            <label htmlFor="priorityId">Priority *</label>
            <select
              id="priorityId"
              name="priorityId"
              value={formData.priorityId}
              onChange={handleChange}
              required
              disabled={!priorities.length}
            >
              {priorities.map(p => (
                <option key={p.id} value={p.id}>{p.name}</option>
              ))}
            </select>
          </div>

            <div className="form-group">
            <label htmlFor="categoryId">Category *</label>
            <select
              id="categoryId"
              name="categoryId"
              value={formData.categoryId}
              onChange={handleChange}
              required
              disabled={!categories.length}
            >
              {categories.map(c => (
                <option key={c.id} value={c.id}>{c.name}</option>
              ))}
            </select>
          </div>

          {error && (
            <div style={{ color: 'var(--danger)', marginBottom: '1rem' }}>
              Error: {error}
            </div>
          )}

          <button
            type="submit"
            className="btn-primary"
            disabled={loading}
          >
            {loading ? 'Creating...' : 'Create Ticket'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateTicket;
